mapper.select:
SELECT

FROM erp_service_message_template a

WHERE a.id = #{id}

SELECT

FROM erp_service_message_template a


    a.del_flag = #{DEL_FLAG_NORMAL}



        ORDER BY ${page.orderBy}


        ORDER BY a.update_date DESC

SELECT

FROM erp_service_message_template a


    a.del_flag = #{DEL_FLAG_NORMAL}



        ORDER BY ${page.orderBy}


        ORDER BY a.update_date DESC

mapper.update:
UPDATE erp_service_message_template
content = #{content}, type = #{type}, link_id = #{linkId}, task_definition_keys = #{taskDefinitionKeys}, task_key_type = #{taskKeyType}, end_intervals = #{endIntervals}, service_type = #{serviceType}, remarks = #{remarks}, update_by = #{updateBy.id}, update_date = #{updateDate},
WHERE id = #{id}

UPDATE erp_service_message_template SET
    del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
