mapper.update:
UPDATE erp_order_good_service_info SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

mapper.select:
     SELECT 

		FROM erp_order_good_service_info a
		WHERE a.order_id = #{orderId}

SELECT a.service_item_id "serviceItemId",SUM(a.pending_num) "pendingNum" FROM erp_order_good_service_info a LEFT JOIN erp_order_original_info orderInfo on a.order_id = orderInfo.id WHERE a.del_flag = 0
AND orderInfo.shop_id = #{zhangbeiId}
AND a.service_item_id in
#{itemIdItem}
GROUP BY a.service_item_id
