mapper.select:
SELECT 

FROM erp_order_split_comment_a_conf a

WHERE a.id = #{id}

SELECT 

FROM erp_order_split_comment_a_conf a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM erp_order_split_comment_a_conf a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
